### Color by max value
def colordict(keys, values=None, cycle=True):
    if values is None:
#        values = ['tab:blue', 'tab:green','tab:orange', 'tab:red', 'tab:purple',
#                  'tab:brown', 'tab:pink', 'tab:gray', 'tab:olive','tab:cyan']

#        values = plt.rcParamsDefault['axes.prop_cycle'].by_key()['color']

        values = plt.cm.viridis.colors[::255//len(keys)]

#        values = [[int(value)/255 for value in rgb[4:][:-1].split(',')] for rgb in colorbrewer['YlGnBu'][9]]

    if len(keys) > len(values):
        if cycle == True:
            values = [values[k%len(values)] for k in range(len(keys))]
        else:
            raise IndexError('More colors were request than are available. Set cycle to True')
    colors = dict(zip(keys, values))
    return colors

def colorby(data, by='max', cycle=False):
    maxlist = (data.max()-data.max().min())/(data.max().max()-data.max().min())
    colors = colordict(maxlist.sort_values(ascending=False).index, cycle=cycle)
    return colors

data = -df
plt.figure()
colors = colorby(data, 'max', True)
for tcn in data.columns[:15]:
    plt.plot(data.loc[:,tcn], color=colors[tcn])
